// TUTORIAL AT MINUTE 13


import React, { useReducer, useState } from 'react';
import './App.css';
import Todo from './Todos'

let count = 0 ;
// with reducers you pass in a type which is the thing you want to do - which case you want to use
// and then you pass in a payload which is the details you want your case to use

const ACTIONS = {

NEW_TODO : 'new todo',

TOGGLE_TODO : 'toggle todo',

DELETE_TODO : 'delete todo',

}



function App() {


const [todos, dispatch] = useReducer(reducer ,[]);
const [name, setName] = useState('')



function reducer(todos, action) {

console.log({action})

switch(action.type){


case ACTIONS.TOGGLE_TODO:
  return todos.map( todo =>{

if(todo.id === action.payload.id){
  return { ...todo, complete:!todo.complete  }
}

return todo

  });


  case ACTIONS.NEW_TODO:
    console.log('fired!')
    return [ ...todos, newTodo(action.payload.name)]




// case ACTIONS.DELETE_TODO:
//   return deleteIt(action.payload.id)



}
}

///todos.map( todo => todo.id === action.payload.id ? { ...todo, complete:!todo.complete }  : todo  )



// function deleteIt(id){

// todos.filter( todo => todo.id !== id  )

// }

function newTodo(name){



count += 1;
console.log({ id: count , name:name})
return { id: count , name:name, complete:false }


}




function handleSubmit(e) {

e.preventDefault()
dispatch({type: ACTIONS.NEW_TODO , payload:{ name:name }})

setName('')

}



// payload is a propertu that gets passed into your dispatch/reducer along with type,
// type will switch you to the correct case in you dispatch/reducer and payload will have all the details that that case's function will need - payload is itself an object of all the properties / variables you will need in your case function



// try also with a wrapped invocation  form-widgets
  return (
    <div className='container'>
      <form action="" onSubmit={handleSubmit}  >



<input 
type="text"
value={name}
onChange={ e => setName(e.target.value ) }
/>

{todos.map(todo => <Todo key={todo.id} todo={todo} dispatch={dispatch}/>)}



      </form>
    </div>
  );
}

// Pass in a reference to a callback that invokes dispatch not a direct dispatch invocation

export default App;
export {ACTIONS}
