import React from 'react'
import {ACTIONS} from './App'

export default function Todo({ todo , dispatch  }) {
    return (



        <div>

<span style={ {color: todo.completed ? 'red' : 'blue' } }  > {todo.name}  </span>
<button onClick={ ()=> {dispatch({  type: ACTIONS.TOGGLE_TODO ,payload: todo.id })}  }  >TOGGLE</button>
<button>COMPLETE</button>

        </div>
    )
}


