import React, { useReducer } from 'react';
import './App.css';




function App() {





  const [count, setCount] = useState(0);

  function increment() {
    setCount(prevCount => prevCount + 1)
  };

  function decrement() {
    setCount(prevCount => prevCount - 1)
  };

  return (
    <div className='container'>
      <button onClick={decrement}>-</button>
      <span>{count}</span>
      <button onClick={increment}>+</button>
    </div>
  );
}

export default App;
